import { useEffect, useState } from "react";
import PaypalCheckoutButton from "../paypal/PaypalCheckoutButton";
import { useNavigate } from "react-router-dom";
import upBtn from "../assets/upBtn.png";
import ShopItem from "../components/ShopItem";
import "./styles/homeStyle.css";

function Home() {
    const items = [{
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/DigitalPoltergeistMainE2_720x.png?v=1667081776",
        price: 42,
        name: "Clones t-shirt"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/CSH01E2_720x.png?v=1661039920",
        price: 42,
        name: "dark hoodie"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/EHP02e1_720x.png?v=1661040011",
        price: 42,
        name: "army gear"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/PADDEDVESTMAIN_720x.png?v=1669329878",
        price: 42,
        name: "vest"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/CargoPantsLessWrinkle_720x.png?v=1661039983",
        price: 42,
        name: "black pants v3"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/20MMFIDLOCKBELTFOLDED_720x.png?v=1663024184",
        price: 42,
        name: "army gear v2"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/RDS01HIGHDETAIL_720x.png?v=1661039997",
        price: 42,
        name: "glitch beat"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/EmbroShirtMainE4_720x.png?v=1663658530",
        price: 42,
        name: "black shirt"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/WoolCapMain_9c673ecf-5307-4691-bf11-2867c8ca1f7b_720x.png?v=1666291207",
        price: 42,
        name: "fuzzy"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/925RingNoShadow3_720x.png?v=1663025028",
        price: 42,
        name: "ring of power"
    },
    {
        image: "https://cdn.shopify.com/s/files/1/1672/7095/products/SKATEBOARDRAWEDITS_1_720x.png?v=1661039953",
        price: 42,
        name: "sk8"
    },
    ]
    const navigate = useNavigate();

    useEffect(() => {
        function preventBehavior(e) {
            e.preventDefault();
        };

        document.addEventListener("touchmove", preventBehavior, { passive: false });
    }, []);
    let open = false;
    const product = {
        description: "zectangles",
        price: 19
    }

    function rippleClick(e) {
        // window.test(e.clientX + 75, e.clientY + 50);
        window.test((window.innerWidth / 2) + 75, (window.innerHeight / 2) + 50);
    }

    // function openPage(state) {
    //   document.getElementById("bg").style.transform = "scaleY(1)";
    //   document.getElementById("mainFooter").style.top = "15px";
    //   rotateBtn();
    // }

    function openPage() {
        if (open === true) {
            window.history.replaceState(null, "New Page Title", "/")
            document.getElementById("mainFooter").style.top = "calc(100vh - 76px)";
            setTimeout(() => {
                document.getElementById("homeSlide").style.transform = "scaleY(0)";
            }, 40);
            document.getElementById("upBtn").style.transform = "rotate(0deg)";
            document.getElementById("itemArea").style.opacity = "0%";
        } else {
            window.history.replaceState(null, "New Page Title", "/shop")

            document.getElementById("mainFooter").style.top = "12px";
            document.getElementById("homeSlide").style.transform = "scaleY(1)";
            document.getElementById("upBtn").style.transform = "rotate(180deg)";

            setTimeout(() => {
                document.getElementById("itemArea").style.opacity = "100%";
            }, 450);
        }
        open = !open;
